package dp.interpreter;// <command> ::= <repeat> | <primitive>public class Command implements Node{	private Node node;		@Override	public void parse(Context context){		if(context.currentToken().equals("REPEAT")){			node = new Repeat();			node.parse(context);		} else {			node = new Primitive();			node.parse(context);		}	}	@Override	public void execute(){		this.node.execute();	}}