package dp.interpreter;public class Repeat implements Node{	private int number;	private Node commandList;		@Override	public void parse(Context context){		context.skipToken("REPEAT");		this.number = context.currentNumber();		context.nextToken();		this.commandList = new CommandList();		this.commandList.parse(context);	}		@Override	public void execute(){		for(int i = 0; i < this.number; i++){			this.commandList.execute();		}	}}