package dp.memento.backup;import java.util.*;// Originatorpublic class Application {	private String state = "default setting";		// return new Memento(state);	Backup backup(){		return new Backup(this.state);	}		// state = m.getState();	void recover(Backup backup){		this.state = backup.getState();	}		void setState(String state){		this.state = state;	}		String getState(){		return state;	}}